import React, { Component } from 'react';
import './App.css';
import Box from './component/Box';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHandBackFist, faHandScissors, faHand} from '@fortawesome/free-regular-svg-icons'; // 아이콘을 가져옴


//1. 박스 2개(타이틀, 사진, 결과)
//2. 가위 바위 보 버튼이 있다.
//3. 버튼을 클릭하면 클릭한 값이 박스에 보임/
//4. 컴퓨터는 랜덤하게 아이템 선택이 된다.
//5. 3 4의 결과를 가지고 누가 이겼는지 승패를 따진다. 
//6. 승패결과에 따라 테두리 색이 바뀐다.(이기면-초록, 지면-빨강, 비기면-검은색)

const choice = {
  rock: {
    name:"Rock",
    img:"data:image/jpeg;base64,",
  },
  scissors:{
    name:"Scissors",
    img:"data:image/jpeg;base64,",
  },
  paper: {
    name:"Paper",
    img:"data:image/jpeg;base64,",
  },
};

export default class AppClass extends Component {
  constructor() {
    super();
    this.state = {
      userSelect: null,
      computerSelect: null,
      result: "",
    };
  }

  // 유저 아이템 선택 시작 // 사용자, 컴퓨터 선택 기능 구현
  play = (userChoice) => {
    const computerChoice = this.randomChoice();
    this.setState({
      userSelect: choice[userChoice],
      computerSelect: computerChoice,
      result: this.judgement(choice[userChoice], computerChoice),
    });
  };

  // 컴퓨터 아이템 선택 시작
  randomChoice = () => {
    const itemArray = Object.keys(choice); //객체에 키값만 뽑아서 어레이로 만들어주는 함수
    const randomItem = Math.floor(Math.random() * itemArray.length);
    const final = itemArray[randomItem];
    return choice[final];
  };

  judgement = (user, computer) => {
    // 1. user == computer tie(비김)
    // 2. user == “rock”, computer == “scissors” user 이김
    // 3. user == “rock”, computer == “paper” user 졌음
    // 4. user == “scissors”, computer == “paper” user 이김
    // 5. user ==  “scissors”,  computer == “rock” user 졌음
    // 6. user == “paper”, computer “rock” user 이김
    // 7. user == “paper”, computer “scissors” user 졌음

    if (user.name == computer.name) {
      return "tie";
    } else if (user.name == "Rock")
      return computer.name == "Scissors" ? "win" : "Lose";
    else if (user.name == "Scissors")
      return computer.name == "Paper" ? "win" : "Lose";
    else if (user.name == "Paper")
      return computer.name == "Rock" ? "win" : "Lose";
  };

  render() {
    return (
    <div className='container'>
      <h1 className='main'>가위 바위 보 게임</h1>
      <div className='main'>
        <Box title = "You" item={this.state.userSelect} result={this.state.result}/>
        <Box title = "Computer" item={this.state.computerSelect} result={this.state.result}/>
      </div>
      <div className='main'>
        <button onClick={() => this.play("rock")}><FontAwesomeIcon icon={faHandBackFist} className="icon"/></button>
        <button onClick={() => this.play("scissors")}><FontAwesomeIcon icon={faHandScissors} className="icon"/></button>
        <button onClick={() => this.play("paper")}><FontAwesomeIcon icon={faHand} className="icon"/></button>
      </div>
    </div>
    )
  }
}


 
